using System;using System.IO;using System.Linq;using System.Text;class X{static public void Main(string[] args){var b = "";var s = Encoding.UTF8.GetString(Convert.FromBase64String(b));s = string.Format(s, b);var n = int.Parse(Console.ReadLine());if (n < 0){s = new string(s.ToCharArray().Reverse().ToArray());n = -n;}while (n-- > 0)Console.Write(s);}}
